$user_skills = isset($_POST['user_skills']) ? $_POST['user_skills'] : [];

// $user_skills = ['web_design', 'frontend_dev', 'marketing']

// Validate at least one selected
if (empty($user_skills)) {
  return new WP_Error('no_skills', 'Please select at least one skill.');
}

// Sanitize and store
$sanitized_skills = array_map('sanitize_text_field', $user_skills);
update_user_meta($user_id, 'skills', $sanitized_skills);

// Or store as comma-separated string
$skills_string = implode(', ', $sanitized_skills);
update_user_meta($user_id, 'skills', $skills_string);

// Services with prices encoded in fldValue: gift_wrap_5, express_ship_15, warranty_25
$selected_services = isset($_POST['product_services']) ? $_POST['product_services'] : [];

// Calculate total add-on cost
$total_addon_cost = 0;
$service_names = [];

foreach ($selected_services as $service) {
  // Extract price from value (e.g., 'gift_wrap_5' → 5)
  preg_match('/_([0-9]+)$/', $service, $matches);
  if (!empty($matches[1])) {
    $total_addon_cost += (int)$matches[1];
  }

  // Convert to readable name (gift_wrap_5 → Gift Wrapping)
  $service_name = str_replace('_', ' ', preg_replace('/_[0-9]+$/', '', $service));
  $service_names[] = ucwords($service_name);
}

