// Log GDPR consent for audit trail
$consents = isset($_POST['gdpr_consent']) ? $_POST['gdpr_consent'] : [];

$consent_data = [
  'user_id' => get_current_user_id(),
  'timestamp' => current_time('mysql'),
  'ip_address' => $_SERVER['REMOTE_ADDR'],
  'user_agent' => $_SERVER['HTTP_USER_AGENT'],
  'consents' => [
    'terms_accepted' => in_array('terms_required', $consents),
    'marketing_opt_in' => in_array('marketing_opt_in', $consents),
    'analytics_cookies' => in_array('analytics_cookies', $consents),
    'third_party_sharing' => in_array('third_party_sharing', $consents),
  ]
];

// Store in custom table for audit
global $wpdb;
$wpdb->insert(
  $wpdb->prefix . 'gdpr_consent_log',
  $consent_data,
  ['%d', '%s', '%s', '%s', '%s']
);

// Also update user meta for quick access
update_user_meta($user_id, 'gdpr_consents', $consent_data['consents']);

// Dynamically generate checkbox options from WordPress categories
