// Format number for display after submission
function format_number_with_commas($number) {
  return number_format((int)$number);
}

// Usage
$team_size = $_POST['team_size']; // '5000'
$formatted = format_number_with_commas($team_size); // '5,000'
echo "Team Size: " . $formatted;

// Calculate age from birth year
function calculate_age_from_year($birth_year) {
  $current_year = date('Y');
  return $current_year - $birth_year;
}

// Usage
$birth_year = $_POST['birth_year']; // '1990'
$age = calculate_age_from_year($birth_year); // 35 (in 2025)

if ($age < 18) {
  return new WP_Error('too_young', 'You must be at least 18 years old.');
}

// Validate US ZIP code range
function validate_us_zip($zip) {
  $zip = (int)$zip;

  if ($zip < 501 || $zip > 99950) {
    return new WP_Error('invalid_zip', 'Please enter a valid US ZIP code.');
  }

  // Optional: Check if ZIP exists in database of valid ZIPs
  // $valid_zips = get_option('valid_zip_codes');
  // if (!in_array($zip, $valid_zips)) {
  //   return new WP_Error('zip_not_found', 'ZIP code not found.');
  // }

  return true;
}

// Dynamically set max quantity based on product stock
// In PHP (render time)
$product_id = get_the_ID();
$stock_quantity = get_post_meta($product_id, '_stock', true);

// Pass to block attributes
$attributes = [
  'maxNum' => (string)$stock_quantity,
  'desctxt' => "We have {$stock_quantity} units in stock."
];

// Or via JavaScript (dynamic update)
<script>
const stockQuantity = <?php echo $stock_quantity; ?>;
const quantityField = document.querySelector('[name="product_quantity"]');
quantityField.setAttribute('max', stockQuantity);
