// Radio button field submits as single string value (not array like checkboxes)
$referral_source = isset($_POST['referral_source']) ? sanitize_text_field($_POST['referral_source']) : '';

// $referral_source = 'search_engine' (single value, not array)

// Validate selection made if required
if (empty($referral_source)) {
  return new WP_Error('no_selection', 'Please select how you heard about us.');
}

// Store the selection
update_user_meta($user_id, 'referral_source', $referral_source);

// Or update database
global $wpdb;
$wpdb->update(
  $wpdb->prefix . 'leads',
  ['referral_source' => $referral_source],
  ['user_id' => $user_id],
  ['%s'],
  ['%d']
);

// Shipping method with price encoded in value: standard_5, express_15, overnight_25
$shipping_method = isset($_POST['shipping_method']) ? sanitize_text_field($_POST['shipping_method']) : '';

if (empty($shipping_method)) {
  return new WP_Error('no_shipping', 'Please select a shipping method.');
}

// Extract price from value (e.g., 'standard_5' → 5)
preg_match('/_([0-9]+)$/', $shipping_method, $matches);
$shipping_cost = !empty($matches[1]) ? (int)$matches[1] : 0;

// Calculate total
$product_total = 100; // Example product price
$order_total = $product_total + $shipping_cost;

// Store shipping info
update_post_meta($order_id, 'shipping_method', $shipping_method);
update_post_meta($order_id, 'shipping_cost', $shipping_cost);
update_post_meta($order_id, 'order_total', $order_total);

echo "Shipping: $" . $shipping_cost;
echo "Total: $" . $order_total;

// JavaScript to show different fields based on payment method selection
const paymentRadios = document.querySelectorAll('[name="payment_method"]');
const creditCardFields = document.getElementById('credit-card-fields');
const paypalFields = document.getElementById('paypal-fields');
const bankFields = document.getElementById('bank-transfer-fields');

paymentRadios.forEach(radio => {
  radio.addEventListener('change', function() {
    // Hide all payment fields
    creditCardFields.style.display = 'none';
    paypalFields.style.display = 'none';
    bankFields.style.display = 'none';

    // Show relevant fields based on selection
    if (this.value === 'credit_card') {
      creditCardFields.style.display = 'block';
    } else if (this.value === 'paypal') {
      paypalFields.style.display = 'block';
    } else if (this.value === 'bank_transfer') {
      bankFields.style.display = 'block';
    }
  });
});

// Trigger change on page load to show default selection
document.querySelector('[name="payment_method"]:checked')?.dispatchEvent(new Event('change'));

// Calculate average service rating from survey responses
function calculate_average_rating($form_id) {
  global $wpdb;

  $ratings = $wpdb->get_col($wpdb->prepare(
    "SELECT meta_value FROM {$wpdb->postmeta} 
     WHERE meta_key = 'service_rating' 
     AND post_id IN (SELECT ID FROM {$wpdb->posts} WHERE post_parent = %d)",
    $form_id
  ));

  if (empty($ratings)) {
    return 0;
  }

  // Ratings are numeric strings: '1', '2', '3', '4', '5'
  $total = array_sum(array_map('intval', $ratings));
  $count = count($ratings);
  $average = $total / $count;

  // Store aggregated data
  update_post_meta($form_id, 'average_rating', round($average, 2));
  update_post_meta($form_id, 'total_responses', $count);

  return round($average, 2);
}

// Usage
$avg_rating = calculate_average_rating($form_id);
echo "Average Rating: " . $avg_rating . " / 5.0 ({$count} responses)";
