// Check if URL starts with https:// and has valid domain
const urlInput = document.querySelector('[name="company_website"]');
urlInput.addEventListener('blur', function() {
  const url = this.value;
  if (url && !url.match(/^https:\/\/(www\.)?[-a-zA-Z0-9@:%._\+~#=]{1,256}\.[a-zA-Z0-9()]{1,6}\b/)) {
    this.setCustomValidity('Please enter a valid HTTPS URL');
  } else {
    this.setCustomValidity('');
  }
});

// Auto-add https:// if missing
const urlInput = document.querySelector('[name="portfolio_url"]');
urlInput.addEventListener('blur', function() {
  const url = this.value.trim();
  if (url && !url.match(/^https?:\/\//)) {
    this.value = 'https://' + url;
  }
});

// Validate URL on form submission
function validate_url_field($url) {
  // Check format
  if (!filter_var($url, FILTER_VALIDATE_URL)) {
    return new WP_Error('invalid_url', 'Please enter a valid URL');
  }

  // Check if URL is accessible (optional, can be slow)
  $headers = @get_headers($url);
  if (!$headers || strpos($headers[0], '200') === false) {
    return new WP_Error('unreachable_url', 'URL is not accessible');
  }

  return true;
}

// Extract domain from URL for database indexing
function extract_domain($url) {
  $parsed = parse_url($url);
  return $parsed['host'] ?? '';
}

// Usage in form submission handler
$company_url = $_POST['company_website'];
$company_domain = extract_domain($company_url);
