// JavaScript - pause other players on play
document.addEventListener('DOMContentLoaded', function() {
  const audioPlayers = document.querySelectorAll('.tpgb-audioplay-wrap audio');

  audioPlayers.forEach(player => {
    player.addEventListener('play', function() {
      // Pause all other players
      audioPlayers.forEach(otherPlayer => {
        if (otherPlayer !== player) {
          otherPlayer.pause();
        }
      });
    });
  });
});

// JavaScript - track audio plays
const audio = document.querySelector('.tpgb-audioplay-wrap audio');
const trackTitle = document.querySelector('.tpgb-audioplay-wrap .title').textContent;

audio.addEventListener('play', function() {
  // Track play event
  gtag('event', 'audio_play', {
    'event_category': 'Audio',
    'event_label': trackTitle,
    'value': 1
  });
});

audio.addEventListener('ended', function() {
  // Track completion
  gtag('event', 'audio_complete', {
    'event_category': 'Audio',
    'event_label': trackTitle,
    'value': audio.duration
  });
});

// Track 25%, 50%, 75% progress
let progressTracked = [];
audio.addEventListener('timeupdate', function() {
  const percent = Math.floor((audio.currentTime / audio.duration) * 100);
  [25, 50, 75].forEach(milestone => {
    if (percent >= milestone && !progressTracked.includes(milestone)) {
      progressTracked.push(milestone);
      gtag('event', 'audio_progress', {
        'event_category': 'Audio',
        'event_label': trackTitle,
        'value': milestone
      });
    }
  });
});
