// PHP - populate from ACF
$projects = get_field('portfolio_projects');
$imgRepeater = array();
foreach ($projects as $index => $project) {
  $imgRepeater[] = array(
    '_key' => (string)$index,
    'Rimg' => array('url' => $project['image']['url'], 'Id' => $project['image']['ID']),
    'Rtitle' => $project['title'],
    'RCaption' => $project['description'],
    'RCategory' => $project['category'],
    'Rurl' => array('url' => $project['link'], 'target' => '_blank', 'nofollow' => false)
  );
}
// Pass $imgRepeater to block attributes

// JavaScript - auto-select filter from URL
const urlParams = new URLSearchParams(window.location.search);
const category = urlParams.get('category');
if (category) {
  const filter = document.querySelector(`.tpgb-filter-list a[data-filter='${category}']`);
  if (filter) filter.click();
}

// JavaScript - lazy load images
const images = document.querySelectorAll('.tpgb-gallery-image img[data-src]');
const observer = new IntersectionObserver((entries) => {
  entries.forEach(entry => {
    if (entry.isIntersecting) {
      const img = entry.target;
      img.src = img.dataset.src;
      img.removeAttribute('data-src');
      observer.unobserve(img);
    }
  });
});
images.forEach(img => observer.observe(img));
