progress_bar_customization.code:
/* Progress bar container */
.tp-scroll-navigation-progress {
    height: 4px;
    background: rgba(0, 0, 0, 0.1);
    position: fixed;
    width: 100%;
    z-index: 999;
}

/* Progress bar fill (active) */
.tp-scroll-navigation-progress-fill {
    height: 100%;
    background: linear-gradient(90deg, #667eea, #764ba2); /* Custom gradient */
    transition: width 0.2s ease-out;
}

/* Alternative: Bottom progress bar */
.tp-scroll-navigation-progress.bottom {
    top: auto;
    bottom: 0;
}

active_state_styling.code:
/* Active dot styling (style-1) */
.tp-scroll-navigation-item.active .tp-scroll-navigation-dot {
    background: #667eea; /* Active color */
    transform: scale(1.3); /* Slightly larger */
    box-shadow: 0 0 15px rgba(102, 126, 234, 0.5); /* Glow effect */
}

/* Active text label styling (style-2) */
.tp-scroll-navigation-item.active .tp-scroll-navigation-label {
    color: #667eea;
    font-weight: 700;
    font-size: 1.1em;
}

/* Active icon color */
.tp-scroll-navigation-item.active .tp-scroll-navigation-icon {
    color: #ffffff;
}

