accessibility_toggle.code:
// Respect prefers-reduced-motion
const reducedMotion = window.matchMedia('(prefers-reduced-motion: reduce)');
if (reducedMotion.matches) {
    document.body.classList.add('disable-smooth-scroll');
}

// Toggle button
document.getElementById('toggle-smooth-scroll').addEventListener('click', function() {
    document.body.classList.toggle('disable-smooth-scroll');
    const disabled = document.body.classList.contains('disable-smooth-scroll');
    localStorage.setItem('smoothScrollDisabled', disabled);
});

// Restore preference on page load
if (localStorage.getItem('smoothScrollDisabled') === 'true') {
    document.body.classList.add('disable-smooth-scroll');
}

section_jump_optimization.code:
// Add smooth scroll class to all anchor links
document.querySelectorAll('a[href^="#"]').forEach(anchor => {
    anchor.addEventListener('click', function(e) {
        const targetId = this.getAttribute('href').substring(1);
        const targetElement = document.getElementById(targetId);

        if (targetElement) {
            e.preventDefault();
            // Smooth scroll handled by tp-smooth-scroll block with smNav: true
            window.location.hash = targetId;

            // Add visual feedback
            targetElement.classList.add('section-highlight');
            setTimeout(() => targetElement.classList.remove('section-highlight'), 2000);
        }
    });
});

custom_viewport_css.code:
/* Container for smooth scroll */
.content-area {
    height: 100vh; /* Full viewport height */
    overflow-y: auto; /* Enable scrolling */
    overflow-x: hidden;
}

/* Optional: Hide scrollbar but keep functionality (webkit browsers) */
.content-area::-webkit-scrollbar {
    width: 0;
    display: none;
}

/* Optional: Custom scrollbar styling */
.content-area::-webkit-scrollbar {
    width: 8px;
}
.content-area::-webkit-scrollbar-track {
    background: #f1f1f1;
}
.content-area::-webkit-scrollbar-thumb {
    background: #888;
    border-radius: 4px;
}
.content-area::-webkit-scrollbar-thumb:hover {
    background: #555;
}

performance_monitoring.code:
// Track FPS during smooth scroll
let lastTime = performance.now();
let frames = 0;

function trackFPS() {
    frames++;
    const currentTime = performance.now();
    if (currentTime >= lastTime + 1000) {
        const fps = Math.round(frames * 1000 / (currentTime - lastTime));
        console.log(`Smooth scroll FPS: ${fps}`);
        if (fps < 30) {
            console.warn('Smooth scroll performance degraded - consider reducing aniTime');
        }
        frames = 0;
        lastTime = currentTime;
    }
    requestAnimationFrame(trackFPS);
}

// Start monitoring during scroll
let scrollTimeout;
window.addEventListener('wheel', function() {
    clearTimeout(scrollTimeout);
    requestAnimationFrame(trackFPS);
    scrollTimeout = setTimeout(() => {
        frames = 0; // Stop counting when scroll ends
    }, 300);
});
